// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
//x_227277_zombie_wo_zombie_world_table
//x_227277_zombie_wo_zombie_player
//x_227277_zombie_wo_zombie_entity


var tableName = "x_227277_zombie_wo_zombie_player";
var column_playerAlive = "zombie_player_alive";
answer = 'no';
gs.addInfoMessage('wfRZP_PA.1-- WorkFlow ZombieWorld-Fight-MultiTable Now Running.   ');
try
{
	var tblZombiePlayer = new GlideRecord('x_227277_zombie_wo_zombie_player');
	if(!tblZombiePlayer)
	{
		gs.addErrorMessage('wfRZP_PA.2-- Table name "ZombiePlayer" DOES NOT EXIST !!');
	}	

	if(tblZombiePlayer.canRead())
	{
		gs.addInfoMessage('wfRZP_PA.3-- PlayerTable is READABLE !!');
	}
	else
	{
		gs.addInfoMessage('wfRZP_PA.4-- ERROR -- PlayerTable Not READABLE !!');
	}
	//tblZombiePlayer.addQuery('zombie_player_alive', 'true');
	gs.addInfoMessage('wfRZP_PA.5-- Attempting to Query the "Zombie Player Table without a CLAUSE ..."   ');
	tblZombiePlayer.query();
	gs.addInfoMessage('wfRZP_PA.6-- Zombie Player Table has been Queried.');
	var recordsFound = tblZombiePlayer.getRowCount();
	if(recordsFound > 0)
	{
		while(tblZombiePlayer.next()) 
		{
			gs.addInfoMessage('wfRZP_PA.7-- Found a Record in the Player Table !!');
			if(tblZombiePlayer.zombie_player_alive)
			{
			    gs.addInfoMessage('wfRZP_PA.8A-- Player Alive? : ' + tblZombiePlayer.zombie_player_alive);
			    answer = 'yes';  //comment
			}
			else
			{
			    gs.addInfoMessage('wfRZP_PA.8B-- Player NOT ALIVE !!');
			}
		}
	}
	else
	{
		gs.addInfoMessage('wfRZP_PA.9-- NO RECORDS FOUND IN ZOMBIE WORLD !!');
	}

	
}
catch(e)
{
	gs.addErrorMessage("wfRZP_PA.10: PLAYER_READY_CHECK Exception Encountered While Attempting to create a GlideRecord for Table:" + tableName);
	gs.addErrorMessage(e.message);
	gs.error(e.message);	
}

