answer = 'no';
gs.addInfoMessage('wfRZP_RPHL.reconfirm.1-- WorkFlow ZombieWorld-Fight-MultiTable Now Running.   ');
try
{
	var tblZombiePlayer = new GlideRecord('x_227277_zombie_wo_zombie_player');
	if(!tblZombiePlayer)
	{
		gs.addErrorMessage('wfRZP_RPHL.reconfirm.2-- Table name "ZombiePlayer" DOES NOT EXIST !!');
	}	

	if(!tblZombiePlayer.canRead())
	{
		gs.addErrorMessage('wfRZP_RPHL.reconfirm.3-- ERROR -- PlayerTable Not READABLE !!');
	}
	tblZombiePlayer.query();
	var recordsFound = tblZombiePlayer.getRowCount();
	if(recordsFound > 0)
	{
		while(tblZombiePlayer.next()) 
		{
			if(tblZombiePlayer.zombie_player_location > 0)
			{
			    gs.addInfoMessage('wfRZP_RPHL.reconfirm.4-- Player Successfully UPDATED to Location of: ' + tblZombiePlayer.zombie_player_location);
				gs.addInfoMessage('wfRZP_RPHL.reconfirm.5-- Player Is NOW READY !!');
				tblZombiePlayer.zombie_player_ready = 'true';
				tblZombiePlayer.update();
			    answer = 'yes';  //comment
			}
			else
			{
				answer = 'no';  //comment
			}
		}
	}
	else
	{
		gs.addInfoMessage('wfRZP_RPHL.reconfirm.6-- NO RECORDS FOUND IN ZOMBIE WORLD !!');
	}

	
}
