sys_variable_value.value:
// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
//x_227277_zombie_wo_zombie_world_table
//x_227277_zombie_wo_zombie_player
//x_227277_zombie_wo_zombie_entity


var tableName = "x_227277_zombie_wo_zombie_player";
var column_playerReady = "zombie_player_ready";
answer = 'no';
gs.addInfoMessage('wfZW_PR.1-- WorkFlow ZombieWorld-Fight-MultiTable Now Running.   ');
try
{
	var tblZombiePlayer = new GlideRecord('x_227277_zombie_wo_zombie_player');
	if(!tblZombiePlayer)
	{
		gs.addErrorMessage('wfZW_PR.2-- Table name "ZombiePlayer" DOES NOT EXIST !!');
	}	

	if(tblZombiePlayer.canRead())
	{
		gs.addInfoMessage('wfZW_PR.3-- PlayerTable is READABLE !!');
	}
	else
	{
		gs.addInfoMessage('wfZW_PR.4-- ERROR -- PlayerTable Not READABLE !!');
	}
	//tblZombiePlayer.addQuery('zombie_player_alive', 'true');
	gs.addInfoMessage('wfZW_PR.5-- Attempting to Query the "Zombie Player Table without a CLAUSE ..."   ');
	tblZombiePlayer.query();
	gs.addInfoMessage('wfZW_PR.6-- Zombie Player Table has been Queried.');
	var recordsFound = tblZombiePlayer.getRowCount();
	if(recordsFound > 0)
	{
		while(tblZombiePlayer.next()) 
		{
			gs.addInfoMessage('wfZW_PR.7-- Found a Record in the Player Table !!');
			if(tblZombiePlayer.zombie_player_ready)
			{
			    gs.addInfoMessage('wfZW_PR.8-- Player: ' + tblZombiePlayer.zombie_player_ready);
			    answer = 'yes';  //comment
			}
			else
			{
			    gs.addInfoMessage('wfZW_PR.9-- Player NOT READY !!');
			}
		}
	}
	else
	{
		gs.addInfoMessage('wfZW_PR.10-- NO RECORDS FOUND IN ZOMBIE WORLD !!');
	}

	
}
catch(e)
{
	gs.addErrorMessage("PLAYER_READY_CHECK Exception Encountered While Attempting to create a GlideRecord for Table:" + tableName);
	gs.addErrorMessage(e.message);
	gs.error(e.message);	
}

