getPlayerReadyResult();
function getPlayerReadyResult() 
{
	  //var wf  = new Workflow (); //Get the workflow id 
      //var wfId  = wf.getWorkflowFromName ( "ZombieWorld Example Access Other Table" ) ; 
	  //gs.AddDebugMessage('Can You Se This Value From the SubFlow: ' + wfId.scratchpad.PlayerReadySubFlowResult);
	  //if(wfId.scratchpad.PlayerReadySubFlowResult != true)
	  try
	  {
		  if(workflow)
		  {
			  gs.addInfoMessage('wfZW_PRCCS.0-- the "workflow" variable exists !');
			  if(workflow.scratchpad)
			  {
				  gs.addInfoMessage('wfZW_PRCCS.1-- the "workflow.scratchpad" variable exists !');
				  if(!workflow.scratchpad.myvar) 
				  {   //https://community.servicenow.com/community?id=community_question&sys_id=b3cd0f6ddb9cdbc01dcaf3231f961998
					  gs.addErrorMessage('wfZW_PRCCS.2-- workflow.scratchpad.myvar NOT EXIST.');
				  }
				  else
				  {
					  gs.addErrorMessage('wfZW_PRCCS.3-- workflow.scratchpad.myvar variable exists !');
					  //if (wfId.scratchpad.PlayerReadySubFlowResult == true) 
					  if(workflow.scratchpad.myvar == 'success')
					  {
						 gs.addInfoMessage('wfZW_PRCCS.4-- ScratchPad Variable Found and is set to TRUE.');
						 answer = 'yes';
					  }
					  else
					  {
						  if(workflow.scratchpad.myvar == 'failed')
						  {
							  gs.addErrorMessage('wfZW_PRCCS.5-- ScratchPad Variable Found but is set to "FAILED".');
						  }
						  else if(workflow.scratchpad.myvar == 'nope')
						  {
							  gs.addErrorMessage('wfZW_PRCCS.6-- ScratchPad Variable Found but is set to "NOPE"');
						  }
						  else {
							  gs.addErrorMessage('wfZW_PRCCS.7-- ScratchPad Variable Found but is set to "Nyet"');
						  }
						  answer = 'no';
					  }
				  }
			  }
			  else
			  {
				  gs.addErrorMessage('wfZW_PRCCS.8-- the "workflow.scratchPad"  variable NOT exist.');
			  }
		  }
		  else
		  {
			gs.addErrorMessage('wfZW_PRCCS.9-- the "workflow" variable NOT exist.');
		  }
		  
	  }
	  catch(e)
	  {
		  gs.addErrorMessage('wfZW_PRCCS.10-- Failed with RunTime Exception: ' + e.message);
	  }
	  return answer;
}

