// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
//x_227277_zombie_wo_zombie_world_table
//x_227277_zombie_wo_zombie_player
//x_227277_zombie_wo_zombie_entity


var tableName = "x_227277_zombie_wo_zombie_entity";
var column_zombieEntityReady = "zombie_entity_ready";
answer = 'no';
try
{
	gs.addInfoMessage('wfZW_ZR.1-- Attempt to connect to Table name ' + tableName + ' ...');
	var tblZombieEntity = new GlideRecord('x_227277_zombie_wo_zombie_entity');
	if(!tblZombieEntity)
	{
		gs.addErrorMessage('wfZW_ZR.2-- Table name ' + tableName + ' DOES NOT EXIST !!');
	}	

	if(tblZombieEntity.canRead())
	{
		gs.addInfoMessage('wfZW_ZR.3-- ZombieEntityTable is READABLE !!');
	}
	else
	{
		gs.addInfoMessage('wfZW_ZR.4-- ERROR -- ZombieEntityTable Not READABLE !!');
	}
	tblZombieEntity.addQuery('zombie_entity_ready', 'true'); //TRUE MUST BE A STRING NOT A BOOLEAN !!
	gs.addInfoMessage('wfZW_ZR.5-- Attempting to Query the "Zombie Entity Table..."   ');
	tblZombieEntity.query();
	gs.addInfoMessage('wfZW_ZR.6-- Zombie Entity Table has been Queried.');
	var recordsFound = tblZombieEntity.getRowCount();
	if(recordsFound > 0)
	{
		while(tblZombieEntity.next()) 
		{
			gs.addInfoMessage('wfZW_ZR.7-- Found a Record in the Zombie Entity Table !!');
			if(tblZombieEntity.zombie_entity_ready) 
				//CANT Refer to Column Name Dynamically via some Variable (at least not explicitly with 'dot' notation)
				//Would have to rely on a Function that accepts a variable string for the column name instead.  (ex:  getvalue(<String variable>) 
			{
			    gs.addInfoMessage('wfZW_ZR.8-- ZombieEntity is READY !!');
			    answer = 'yes';  
				//Original Default Comments are Misleading. 
				//YES AND NO values MUST BE STRINGS.  
				//NOT built-in Booleans !
			}
			else
			{
			    gs.addInfoMessage('wfZW_ZR.9-- ZombieEntity NOT READY !!');
			}
		}
	}
	else
	{
		gs.addInfoMessage('wfZW_ZR.10-- NO RECORDS FOUND IN ZOMBIE WORLD !!');
	}

	
}
catch(e)
{
	gs.addErrorMessage("wfZW_ZR.11-- ZOMBIE_ENTITY_READY Exception Encountered While Attempting to create a GlideRecord for Table:" + tableName);
	gs.addErrorMessage(e.message);
	gs.error(e.message);	
}

